function getXHR(){
    var xhrObject = null;
    if(window.XMLHttpRequest){
        xhrObject = new XMLHttpRequest();
    }else{
        alert("Votre navigateur actuelle ne supporte pas les XMLHttpRequest.\n"+
        "Veuillez essayer avec un autre navigateur");
        xhrObject = false;
    }
}
